<?php/** * The template for displaying search results pages. * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#search-result * * @package Astra * @since 1.0.0 */if ( ! defined('ABSPATH')) {    exit; // Exit if accessed directly.}get_header();if (astra_page_layout() == 'left-sidebar') {    get_sidebar();}$config = require(get_theme_file_path('/include/config.php'));$json = json_encode(    array_merge(        ['target' => '#mpInventory'],        $config    ));?>    <div id="primary" <?php astra_primary_class(); ?>>        <div id="mpInventory"></div>        <script>            window.onload = () => {                inventory(<?php echo $json; ?>);            };        </script>    </div><!-- #primary --><?phpif (astra_page_layout() == 'right-sidebar') {    get_sidebar();}get_footer();